var mintableTNT20ContractAddress;

test('should deploy MintableTNT20 contract with args', async () => {
    let ContractToDeploy = new ContractFactory(MintableTNT20ABI, MintableTNT20ByteCode, walletAdmin);
    let result = await ContractToDeploy.deploy("Zuckbuck", "ZB", 2, 100000000, true);
    mintableTNT20ContractAddress = result.contract_address;
    let mintableTNT20Contract = new Contract(mintableTNT20ContractAddress, MintableTNT20ABI, walletAdmin);

    console.log("MintableTNT20 contract is ", mintableTNT20ContractAddress);
    expect(result).not.toBe(null);
    expect(mintableTNT20Contract).not.toBe(null);
    let name = await mintableTNT20Contract.name();
    let symbol = await mintableTNT20Contract.symbol();
    let decimals = await mintableTNT20Contract.decimals();
    let initialSupply = await mintableTNT20Contract.totalSupply();

    expect(name).toBe("Zuckbuck");
    expect(symbol).toBe("ZB");
    expect(decimals).toBe(2);
    expect(initialSupply.toNumber()).toBe(100000000);

    let balanceAdmin = await mintableTNT20Contract.balanceOf(AddressAdmin);
    console.log("Token balance of the admin wallet: ", balanceAdmin.toString());
    expect(balanceAdmin.toNumber()).toBe(100000000);
});

test('should mint 1000.00 tokens to the admin wallet', async () => {
    let mintableTNT20Contract = new Contract(mintableTNT20ContractAddress, MintableTNT20ABI, walletAdmin);
    let supplyBefore = await mintableTNT20Contract.totalSupply();
    let balanceBefore = await mintableTNT20Contract.balanceOf(AddressAdmin);
    console.log("Total supply before minting new tokens: ", supplyBefore.toString());
    console.log("Admin balance before minting new tokens: ", balanceBefore.toString());

    let result = await mintableTNT20Contract.mint(100000);
    expect(result.hash).not.toBe(null);

    let supplyAfter = await mintableTNT20Contract.totalSupply();
    let balanceAfter = await mintableTNT20Contract.balanceOf(AddressAdmin);
    console.log("Total supply after minting new tokens: ", supplyAfter.toString());
    console.log("Admin balance after minting new tokens ", balanceAfter.toString());

    expect(supplyAfter.toNumber()).toBe(supplyBefore.toNumber() + 100000);
    expect(balanceAfter.toNumber()).toBe(balanceBefore.toNumber() + 100000);
});

test('A non-admin user should not be able to mint new tokens', async () => {
    let mintableTNT20Contract1 = new Contract(mintableTNT20ContractAddress, MintableTNT20ABI, walletUser1);
    
    let supplyBefore = await mintableTNT20Contract1.totalSupply();
    let balanceBefore = await mintableTNT20Contract1.balanceOf(AddressUser1);
    console.log("Total supply before minting new tokens: ", supplyBefore.toString());
    console.log("User1 balance before minting new tokens: ", balanceBefore.toString());

    let result = await mintableTNT20Contract1.mint(100000);
    expect(result.hash).not.toBe(null);

    let supplyAfter = await mintableTNT20Contract1.totalSupply();
    let balanceAfter = await mintableTNT20Contract1.balanceOf(AddressUser1);
    console.log("Total supply after minting new tokens: ", supplyAfter.toString());
    console.log("User1 balance after minting new tokens ", balanceAfter.toString());

    expect(supplyAfter.toNumber()).toBe(supplyBefore.toNumber());
    expect(balanceAfter.toNumber()).toBe(balanceBefore.toNumber());
});

test('should transfer 99.00 token to user1', async () => {
    let mintableTNT20Contract = new Contract(mintableTNT20ContractAddress, MintableTNT20ABI, walletAdmin);
    let balanceBefore = await mintableTNT20Contract.balanceOf(AddressUser1);
    let result = await mintableTNT20Contract.transfer(AddressUser1, 9900);
    let balanceAfter = await mintableTNT20Contract.balanceOf(AddressUser1);

    expect(result.hash).not.toBe(null);
    expect(balanceAfter.toNumber()).toBe(balanceBefore.toNumber() + 9900);
});

test('should approve user2 to transfer 2.13 token from user1', async () => {
    let amount = 213;
    let mintableTNT20Contract1 = new Contract(mintableTNT20ContractAddress, MintableTNT20ABI, walletUser1);
    let mintableTNT20Contract2 = new Contract(mintableTNT20ContractAddress, MintableTNT20ABI, walletUser2);

    let balanceBeforeUser1 = await mintableTNT20Contract1.balanceOf(AddressUser1);
    let balanceBeforeUser2 = await mintableTNT20Contract1.balanceOf(AddressUser2);
    await mintableTNT20Contract1.approve(AddressUser2, amount);
    let allowance = await mintableTNT20Contract1.allowance(AddressUser1, AddressUser2);
    console.log("Allowance[user1][user2]:", allowance.toString());

    let result = await mintableTNT20Contract2.transferFrom(AddressUser1, AddressUser2, amount);
    let balanceAfterUser1 = await mintableTNT20Contract1.balanceOf(AddressUser1);
    let balanceAfterUser2 = await mintableTNT20Contract1.balanceOf(AddressUser2);

    expect(result.hash).not.toBe(null);
    expect(allowance.toNumber()).toBe(amount);
    expect(balanceAfterUser1.toNumber()).toBe(balanceBeforeUser1.toNumber() - amount);
    expect(balanceAfterUser2.toNumber()).toBe(balanceBeforeUser2.toNumber() + amount);
});

